	$currLang = 'EN';	$otherLang= &doLang();sub doLang{local(@regLang) =(FI,SE,EN,DE,FR);local (@tempLang);local ($out);for(@regLang){	local($i) = $_;		next if ($i eq $currLang);		print "$i\n";		push (@tempLang,$i);		}	$out =join ('|', @tempLang);return $out;}print "Start\n\n";print "curr : $currLang\n";print "out  : $out\n";print "other: $otherLang\n";print "otARR: @tempLang\n";print "othe2: $i\n";print "regL : @regLang\n";print "end\n\n\n";