#Tjalles skapa toc-file i ett dir-träd!# 11-nov-97-Tjalle - skapad# 12-nov-97-Tjalle - overförd till SDC# 13-nov-97-Tjalle - implemntering av OS-specifikadelar#                    verkar att fungera i Mac och Win#kommentera bort felaktiga OS$OS="MAC";#$OS="WIN";#$OS="UNIX";# OBS Macar klarar inte av: chdir "../"#MACOS	if ($OS=~Ê"MAC"){		$dirSep =':';		$rootDir='FATSO:perl:tjs';		}#WINDOWS	if ($OS=~"WIN")	{		$dirSep ='\\';			$rootDir='H:\sdc\documentation';	}#UNIX					if ($OS=~"UNIX"){		$dirSep ='/';	}$configFile ='toc.cfg';$tocFile = 'toctest.html';$tocTarget= "hankToc";  $mainTarget ='hankText';$fileTypeDeny = "css|gif|cfg|pl";$cfgDir="perl\.cfg";print "\nstart....\n";	opendir(DIR,$rootDir) || die "Can't open current \ndirectory: $rootDir";	@filenames = readdir(DIR);	closedir(DIR);	chdir $rootDir;	#open the toc.file in ROOT dir	$tempTocFile = ">$tocFile";	open (ROOTTOCH, $tempTocFile);	print ROOTTOCH &startHtml($currDir);	print ROOTTOCH "<B>HOME</B><BR><HR>";	print ROOTTOCH "<UL>";for (@filenames) {	chdir $rootDir;	next if $_ eq '.';	next if $_ eq '..';	next if ($_=~ /(.*)frame(.*)/);	next if $_ eq $tocFile;	next if ($_=~ /toc(.*)/);	next if ($_=~ /(.*)\.($fileTypeDeny)/);	#next if ($_=~ /(.*)\.(css|gif|cfg|pl)/);	if (!(-d $_)){		$title = &getTitle($_);		print ROOTTOCH &itemLine($_,$title, "F");	print "RF-$_,$title\n";	}	else{		$dirtitle = &getDirTitle($_);		print ROOTTOCH &itemLine("$_/$tocFile",$dirtitle,"D");		print "RD-$_,$dirtitle \n";		$currPath ="";		$currDir =$_;		$dirinfo=&doFolder($currPath,$currDir,);		chdir "$rootDir$currPath";	}} #filenames	print ROOTTOCH "</UL>";	print ROOTTOCH &endHtml();	close ROOTTOCH;	print "\n....END\n";#subssub doFolder{	local($currPath, $currDir) = @_;#	local (DIR);	local(*TOCH);	local ($a);# open a dir and read	opendir(DIR,$currDir) || die "Can't open current \ndirectory: $CurrDir";	local(	@filename) = readdir(DIR);	closedir(DIR);	chdir $currDir;#open a toc.file in curr dir	$tempTocFile = ">$tocFile";	open (TOCH, $tempTocFile);	print TOCH &startHtml($currDir);	$fullPath= "..\\$currPath";#print "PATH:$currPath\n";#	@dirsUp = split(/$dirSep/,$fullPath);#		for (@dirsUp){#			 $a ="/$_/$tocFile";#			print TOCH "$a\n";}	print TOCH "<B><A HREF=\"\.\.\/$tocFile\" TARGET=\"$tocTarget\">HOME</A>\<\/B\>\<BR\>";		$a=&getTitle($configFile);	print TOCH "<B>$a</B><BR><HR>";	print TOCH "<UL>";	for (@filename) {		next if $_ eq '.';		next if $_ eq '..';		next if ($_=~ /(.*)frame(.*)/);		next if ($_=~ /(.*)\.($fileTypeDeny)/);		next if ($_=~ /toc(.*)/);		if (!(-d $_)){			$title = &getTitle($_);			print TOCH &itemLine($_,$title, "F");				}		else{			$title = &getDirTitle($_);			print TOCH &itemLine("$_/$tocFile",$title,"D");				print  "  $_, $title\n";		$currPath ="$currDir";		$currDir =$_;		&doFolder($currPath,$currDir);	#	chdir "$rootDir$currPath";				print "$rootDir$currPath\n";		}	} # End forLoopprint TOCH "</UL>";print TOCH &endHtml();close TOCH;} #end SUB doFolder# hamtar TITLE i en filsub getTitle {	local($in) = @_;	open(CurrFILE,$in);	$f ="400";	read (CurrFILE,$get,$f);	close CurrFILE;	if($get=~ m/<TITLE>(.*)<\/TITLE>/) {		$title=$1;	} 	else {		$title="undef"	}	return $title;} # end getTitle# hamtar TITLE fran en cfgfil for en dirsub getDirTitle {	local($in) = @_;	local($a);#KOLLA OS Har inte hittat annan lšsning (MACAR behšver : fšr current dir	if ($OS=~"MAC"){$a ="$dirSep$in$dirSep$configFile";}	    #MACOS	if ($OS=~"WIN")	{$a ="$in$dirSep$configFile";}				#WINDOWS	if ($OS=~"UNIX"){$a ="$in$dirSep$configFile";}				#UNIX		print "$a\n";	open(CurrFILE,$a);	$f ="45";	read (CurrFILE,$get,$f);	close CurrFILE;	if($get=~ m/<TITLE>(.*)<\/TITLE>/) {		$dirtitle=$1;	} 	else {		$dirtitle="undef";	}	return $dirtitle;} #end getDirTitle# HTML SUBS, # endast for att printa html!# varken las eller skriv filersub startHtml {	local($in) = @_;	$style ="";	open (STYLE, "$rootDir$dirSep"."$cfgDir$dirSep"."defaultstyle\.cfg");	while (<STYLE>){		$style="$style$_";	}	if($style=~ s/<TITLE>(.*)<\/TITLE>/<TITLE>$demoTitle<\/TITLE>/)		{$Style=$1;} 	if($style=~ s/<BASE TARGET=\"(.*)\">/<BASE TARGET=\"$mainTarget\">/) 		{$Style=$1;} 	close STYLE;	$out ="<HTML>\n<HEAD>\n<TITLE>toc for-$in</TITLE>$style\n<BASE TARGET=\"$mainTarget\"></HEAD>\n<Body onLoad=\"parent.frames[2].location.href='index.html'\;\">\n";	#print "tempout: $style-$rootDir$dirSepperl\.cfg\\defaultstyle\.txt\n";	return $style;} #end StartHtmlsub endHtml {	local($in) = @_;	$out ="\n</BODY>\n</HTML>";	return $out;} #end endHtmlsub itemLine {	local($i1,$i2,$i3) = @_;	if ($i3 =~ "D"){		$out ="<LI><B><A HREF=\"$i1\" TARGET=\"$tocTarget\">$i2</A></B>\n";	} else {		$out ="<LI><A HREF=\"$i1\">$i2</A><BR>\n";	}	return $out;} #end itemLine