#Tjalles läsa Mac folders# 8-nov-97$rootDir='FATSO:perl:';     #MACOS$configFile ='toc.cfg';$currDir ='tjs:';print "\nstart....\n";$currPath = "$rootDir$currDir";@dirsUp = split(/:/,$currPath);for (@dirsUp){		print "$_\n";}	opendir(DIR,$currPath) || die "Can't open current \ndirectory: $currDir";	@filenames = readdir(DIR);	closedir(DIR);for (@filenames) {chdir $currPath;	next if $_ eq '.';	next if $_ eq '..';	next if $_ eq 'toc.cfg';				if (!(-d $_)){					print "$currPath$_";					print "-F-";					print "\n"			}			else{					print "$currPath$_";					print "-Dir-";					print "\n";					$tempPath ="$currPath$_:";					$dirLevel =1;					$dirinfo=&doFolder($tempPath,$dirLevel);			}	} #filenamesprint "\n....END\n";#subssub doFolder{ my($tempPath, $dirLevel) = @_;	chdir $tempPath;	opendir(DIR,$tempPath) || die "Can't open current \ndirectory: $tempPath";	@filename2 = readdir(DIR);	closedir(DIR);			for (@filename2) {						next if $_ eq '.';						next if $_ eq '..';						next if $_ eq 'toc.cfg';						if (!(-d $_)){								print "$tempPath$_";								print "-F-";								print "\n"						}						else{								print "$tempPath$_";								print "-Dir-($dirLevel)";								print "\n";								$nextPath ="$tempPath$_:";							$dirLevel =$dirLevel+1;								$dirinfo=&doFolder($nextPath,$dirLevel);						}					chdir $tempPath;			} # End forLoop} #end SUB